seq<-""
A<-0;G<-0;C<-0;T<-0;
for(i in 1:nchar(seq))
{tmp<-substr(seq,i,i);if(tmp=="A") A=A+1;if(tmp=="G") G=G+1;if(tmp=="C") C=C+1;if(tmp=="T") T=T+1;}
print(paste("Content of A",A,A/nchar(seq)));print(paste("Content of G",G,G/nchar(seq)))
print(paste("Content of C",C,C/nchar(seq)));print(paste("Content of T",T,T/nchar(seq)))
print(paste("Content of GC",G+C,(G+C)/nchar(seq)));print(paste("GC-ratio",A+G+C+T,(A+T)/(G+C)))