seq<-""
Base<-c("A","G","C","T")
num<-c(1:4);for(i in 1:4) num[i]=0;
df<-data.frame(Base,num);for(i in 1:nchar(seq))
{tmp<-substr(seq,i,i);for(i in 1:4){if(tmp==df[i,1]){df[i,2]=df[i,2]+1;break}}}
for(i in 1:4){freq<-df[i,2]/nchar(seq);print(paste(df[i,1],df[i,2],freq))}
print(paste("Content of GC",G+C,(G+C)/nchar(seq)));print(paste("GC-ratio",A+G+C+T,(A+T)/(G+C)))